def getMove(x,y,x2,y2,d,factor):
    l= [" ","left0","left0","left0","left0","left0","left0","left0","left0","right0","right0","right0","right0","right0","right0","right0"]
    uat = d
    goal = getAngle((x,y),(x2,y2))
    global angle
    r = []
    if (l[(goal-uat)%16] == "left0"):
        angle += min((goal-uat)%16,(uat-goal)%16)
    else:
        angle -= min((goal-uat)%16,(uat-goal)%16)
    r = r+[l[(goal-uat)%16]]*min((goal-uat)%16,(uat-goal)%16)
    r.append("up0")
    if factor == 4:
    	r.append("up0")
    return r

